// - Crear un state para el valor del `input1` definirle
// un valor inicial random (1 o 0).
// - Permitir que el usuario pueda cambiarle el valor a 
// `input1` definiendo 1 o 0. Si el usuario cargase otra cosa setear el valor 0.

import { useState } from "react";

const valueInput = String(Math.floor(Math.random() * 1))  

export default function Practica4 () {
    
    const [input, valorInput] = useState(valueInput)
    
    const changeValueInput = (event) => {
        const value = event.target.value
        console.log(value);
        console.log(input);
        value === 1
        ? valorInput(0)
        : valorInput(value)
        return value
    }
    
    return (
        <div className="App">
            <div>
                <label for="input1">Entrada 1:</label>
                <input type="number" id="input1" value={value} onClick={changeValueInput}/>
            </div>
            <div>
                <label><input type="checkbox" /> Entrada 2</label>
            </div>
                <div>
                <label for="selectGate">Compuerta lógica:</label>
                <select id="selectGate"></select>
            </div>
            <div><span>Salida: { /* aca iria el resultado*/ }</span></div>
        </div>
    );
}
