import './App.css';
import Menu from './Menu';
import { useState } from 'react';
import MainContent from './MainContent';




const site = {
  title: 'Unidad 1.2 - Práctica',
  body: 'Implementación de árbol de componentes y propiedades en React.'
}

function App() {
  const [option, setOption] = useState(0);
  const [practiceSelected, setPracticeSelected] = useState('#practica_1');

  return (
    <div>
      <Menu selected={option} onSelect={setOption}>
        <Option value={0}>Opción 0</Option>

        <Option value={1}>Opción 1</Option>

        <Option value={2}>Opción 2</Option>
      </Menu>
      <Menu onOptionSelected={(t) => { setPracticeSelected(t) }} />
      <MainContent practiceSelected={practiceSelected} />
      {
      }
    </div>
  );
}

export default App;
