export default function Form () {

    const [focused, setFocused] = useState(true)
    const inputRef = useRef()
  
   useEffect(() => {
      if (focused) {
        inputRef.current.focus()
      } else {
        inputRef.current.blur()
      }
    }, [focused])
  
     return (
  
         <div>
  
             <label for="input1">Input 1</label>
  
             <input id="input1"/>
  
             <label for="input2">Input 2</label>
  
             <input id="input2" />
  
         </div>
  
     );
  
  }
