import React from 'react'


import { useState } from "react";


const LogicGates = ({ input, changeValueInput, checkbox, changeCheckboxValue }) => {
    const [resultado, setResultValue] = useState(null)
    const [select, setSelectValue] = useState(null)




    const changeSelectValue = (event) => {
        const select = event.target.value
        let result = 0;
        console.log(select);
        switch (select) {
            case "OR":
                result = input * checkbox
                setResultValue(result)
                break;
            case "AND":
                result = input + checkbox
                result >= 1
                ? setResultValue(1)
                : setResultValue(0)
                break;
            case "NAND":
                result = input + checkbox
                result == 0
                ? setResultValue(1)
                : setResultValue(0)
                
                break;
            case "NOR":
                result = input * checkbox
                result == 0
                ? setResultValue(1)
                : setResultValue(0)
                break;
            case "XOR":
                result = input + checkbox
                if (input == 0 && checkbox == 0) { result = 0 };
                if (input == 1 && checkbox == 1) { result = 0 };
                setResultValue(result)
                break;
            default:
                //
        }

    }
    return (
        <div>            

            <div>
                <label for="selectGate">Compuerta lógica:</label>
                <select id="selectGate" value={select} onChange={changeSelectValue}>
                    <option value="OR">OR</option>
                    <option value="AND">AND</option>
                    <option value="NAND">NAND</option>
                    <option value="NOR">NOR</option>
                    <option value="XOR">XOR</option>
                </select>

            </div>
            <div><span>Salida: {resultado}</span></div></div>
    )
}

export default LogicGates
