const MenuContext = createContext();
const MenuDispatchContext = createContext();

export default function Menu({ onOptionSelected, children, selected, onSelect }) {
    const onClickOptionHandler = (event) => {
        event.preventDefault();
        onOptionSelected(event.target.hash);
    };

    const valueSelected = selected;

    const onDispatchHandler = (value) => {
        onSelect(value);
    };

    return (
        <div>
            <h1>TUP - UTN - Laboratorio de computación 3 - Práctica</h1>
            <MenuContext.Provider value={valueSelected}>
                <MenuDispatchContext.Provider value={onDispatchHandler}>
                    {children}
                </MenuDispatchContext.Provider>
            </MenuContext.Provider>
        </div>
    );
}
