// Input y obtener este valor
// botón guardar esto en un arreglo

import { useState } from "react";

// renderizar arreglo

import React from 'react'
import AddArray from "./AddArray";

const Input = () => {
    const [inputValue, setInputValue] = useState("");
    const [array, setArray] = useState([]);

    const onInputChange = (event) => {
        setInputValue(event.target.value)
    };

    const onAddButtonClick = () => {
        if (inputValue != "") {
            setArray([...array, inputValue]);
            setInputValue("");
        }
    };


    const OnDeleteArray = (index) => {

        let arrayToAddInfo = array.slice(0);
        arrayToAddInfo.splice(index, 0);
        setArray([arrayToAddInfo]);

    }

    return (
        <div>
            <input onChange={onInputChange} value={inputValue} type="text" />
            <button onClick={onAddButtonClick} type="button" >Enviar</button>
            <AddArray array={array} deleteArrayItem={OnDeleteArray} />
        </div>
    );
}

export default Input
